<span class="line">      <span class="token punctuation">.</span><span class="token function">replace</span><span class="token punctuation">(</span><span class="token regex"><span class="token regex-delimiter">/</span><span class="token regex-source language-regex">^整$</span><span class="token regex-delimiter">/</span></span><span class="token punctuation">,</span> <span class="token string">'零元整'</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
